        g2d.dispose();

//        Raster raster = bufferedImage.getRaster();
//        DataBufferByte dataBufferByte = (DataBufferByte) raster.getDataBuffer();
//        byte[] data = dataBufferByte.getData();
//        return data;

        return bufferedImage;
