    public double remap (double value, double old_min, double old_max, double new_min, double new_max ) {

        return ( (value - old_min) / (old_max - old_min) ) * (new_max - new_min) + new_min;
    }

    public double remap (double value) {
        return remap(value, 0, 4224, 0, TILE_SIZE);
    }

    private BufferedImage drawImage(Map<String, List<Geometry>> layers, int zoom) {
        double maxX = 0;
        double maxY = 0;

        // Constructs a BufferedImage of one of the predefined image types.
        BufferedImage bufferedImage = new BufferedImage(TILE_SIZE, TILE_SIZE, BufferedImage.TYPE_INT_RGB);
        // Create a graphics which can be used to draw into the buffered image
        Graphics2D g2d = bufferedImage.createGraphics();
        g2d.setPaint ( Color.decode(TILE_COLOR_DEFAULT));
        g2d.fillRect ( 0, 0, TILE_SIZE, TILE_SIZE);

        g2d.setStroke(stroke(zoom));
        g2d.setColor(Color.BLACK);

        Set<String> streetsAddedAlready = new HashSet<>();
        for (Map.Entry<String, List<Geometry>> geometry : layers.entrySet()) {
            String key = geometry.getKey();
            List<Geometry> value = geometry.getValue();
            for (Geometry geometry1 : value) {

//                LinkedHashMap userData = (LinkedHashMap) geometry1.getUserData();
//                if (!userData.isEmpty() && userData.containsKey("name")) {
//                    System.out.println("sss");
//                    String name = userData.get("name").toString();
//                    if (!streetsAddedAlready.contains(name)) {
//                        g2d.setPaint(Color.BLACK);
//                        g2d.setFont(new Font("Serif", Font.BOLD, 20));
//                        FontMetrics fm = g2d.getFontMetrics();
//                        int x = (int) geometry1.getCoordinate().x;
//                        int y = (int) geometry1.getCoordinate().x;
//                        g2d.drawString(name, x, y);
//                        streetsAddedAlready.add(name);
//                    }
//                }

                Coordinate last = null;
                for (Coordinate coordinate : geometry1.getCoordinates()) {
                    if (last == null) {
                        last = coordinate;
                        continue;
                    }
                    // g2d.draw(new Line2D.Double(last.x, last.y, coordinate.x, coordinate.y));
                    g2d.draw(new Line2D.Double(remap(last.x), remap(last.y), remap(coordinate.x), remap(coordinate.y)));
                    last=coordinate;

                    if (last.x > maxX) {
                        maxX = last.x;
                    }
                    if (last.y > maxY) {
                        maxY = last.y;
                    }
                }
            }

        }

//        System.out.println(maxX);
//        System.out.println(maxY);


        g2d.dispose();
        g2d.setBackground(Color.YELLOW);

        return bufferedImage;
    }

    private Stroke stroke(int zoom) {
        switch (zoom) {
            case 14:
                return new BasicStroke(1);
            default:
                return new BasicStroke(1);
        }
    }

    @GetMapping("/tile/v1/car/tile({x},{y},{z})")
    public byte[] getTile(@PathVariable("x") int x,
                          @PathVariable("y") int y,
                          @PathVariable("z") int z) throws IOException, URISyntaxException {
        URL imageUrl = new URL("https://www.logaster.com/blog/wp-content/uploads/2013/06/jpg.png");
        BufferedImage bufferedImage = ImageIO.read(imageUrl);
        bufferedImage =  drawTest(x, y, z);
        ByteArrayOutputStream baos=new ByteArrayOutputStream();

        ImageIO.write(bufferedImage, "png", baos);
        baos.flush();



        byte[] encode = Base64.getEncoder().encode(baos.toByteArray());
        baos.close();

        byte[] decodeBase64 = Base64.getDecoder().decode(encode);
        return decodeBase64;
    }




